import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';


const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  bigAvatar: {
    width: 60,
    height: 60,
    padding: '1em',
    margin: "0"
  },
}));




export default function PupilCard(props) {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={
          <Avatar alt={props.firstname + " " + props.surname} src={require("../../assets/pupilPhotos/user.png")} className={classes.bigAvatar} />
        }
        title={props.firstname + ' ' + props.surname}
        subheader={`Behavior points: ${props.behaviourPoints}`} />
      <CardContent>
        <Typography>
        Assessment 1: {props.assessmentOne}
        </Typography>        
        <Typography>
        Assessment 2: {props.assessmentTwo}
        </Typography>        
        <Typography>
        Assessment 3: {props.assessmentThree}
        </Typography> 
      </CardContent>      
      <div>

      </div>
    </Card>

  )
}
