import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';


const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  bigAvatar: {
    width: 60,
    height: 60,
    padding: '1em',
    margin: "0"
  },
  red: {
    backgroundColor: "red"
  }
}));




export default function PupilCard(props) {
  const classes = useStyles();
  const behaviourPoints = props.behaviourPoints;
  // const setHeaderColour = (props.behaviourPoints) =>{
  //   if(props.behaviourPoints < 0){
  //     return classes.red
  //   }
  // }
   console.log(behaviourPoints);


  return (
    <Card className={classes.root}>
      <CardHeader className={behaviourPoints < 0 ? 'classes.red' : ''}
        title={props.firstname + ' ' + props.surname}
        subheader={`Behavior points: ${props.behaviourPoints}`} 
      />
      <CardMedia
        className={classes.media}
        image={require("../../assets/pupilPhotos/user.png")}
        title={props.firstname + ' ' + props.surname}
      />
      <CardContent>
        <Typography paragraph>
          Assessment 1: {props.assessmentOne}
        </Typography>        
        <Typography paragraph>
          Assessment 2: {props.assessmentTwo}
        </Typography>        
        <Typography paragraph>
          Assessment 3: {props.assessmentThree}
        </Typography> 
      </CardContent>      
      <div>

      </div>
    </Card>

  )
}
